/* com.fw.core.mapper.db1.bo.BoCommonMapper.selectCommonCd, 공통코드 리스트 조회, 22.09.25, sjpaik , BoCommonMapper.xml */ SELECT group_cd , cd , group_nm , upper_cd , cd_nm , cd_order , cd_desc , cd_level , use_flag , create_seq , create_dt , update_seq , update_dt FROM tb_common_cd WHERE del_flag = 'N' AND use_flag = 'Y' AND upper_cd = #{groupCd}
AND cd_replace1 = #{cdReplace1}
ORDER BY cd_order ASC
